  const [name, dispacthName] = useReducer()


  return (
    <div className={classes['form-container']}>

      <form className={classes['form-container__form']}>
          <div className={classes['form-container__form__item']}>
            <input type='text' placeholder="First Name"></input>
            <img src={alert} alt='error'></img>
          </div>

          <div className={classes['form-container__form__item']}>
          <input type='text' placeholder="Last Name"></input>
          </div>

          <div className={classes['form-container__form__item']}>
          <input type='email' placeholder="Email Address"></input>
          </div>

          <div className={classes['form-container__form__item']}>
          <input type='password' placeholder="Password"></input>
          </div>

          <Button className={classes['form-container__form__btn']}>Claim your free trial</Button>
      </form>

      <p>By clicking the button, you are agreeing to our <span>Terms and Services</span></p>

    </div>
  )
}


export default Form;
